UINT32 GetCurFileNum(UINT32 num)
{
     FILEDB_HANDLE     FileDBHdl = 0;
    PFILEDB_FILE_ATTR FileAttr = NULL;
    INT32             ret;
    UINT32             i,j;
    FileDB_Refresh(FileDBHdl);
    j = 0;
        for (i=0;i< PLAYBACK_Totlefilenum;i++)
        {
            
            FileAttr = FileDB_SearhFile2(FileDBHdl,i);
            FileSys_WaitFinish();    
            debug_msg("^Y GetCurFileNum  i = %d  name :%s \r\n",i,FileAttr->filename);
            if(GetPlaybackFileType() == MovieFile)
            {
                if(FileAttr->fileType == FILEDB_FMT_MOV)//if(FileAttr->fileType == FILEDB_FMT_JPG)
                {           
                    
                    if(j>= num)
                        break;
                    j++; 
                }
            }
            else if(GetPlaybackFileType() == PhotoFile)
            {
                if(FileAttr->fileType == FILEDB_FMT_JPG)
                {
                
                if(j>= num)
                        break;
                j++; 
                }
            }
                
        }
        debug_msg("^Y GetCurFileNum  %d : %d: %d \r\n",num,j,i );
    return i ;
}

