INT32 UIFlowWndPlayFileList_OnOpen(VControl *pCtrl, UINT32 paramNum, UINT32 *paramArray)
{
FILEDB_HANDLE     FileDBHdl = 0;
    PFILEDB_FILE_ATTR FileAttr = NULL;
    if(paramNum > 0)
    {
        PLAYBACK_WindowPage = 0;//CheackFilePage(paramArray[1]);
        debug_ind(("UIFlowWndPlayFileList_OnOpen  File num: %d\r\n", PLAYBACK_WindowPage));
        
    }
    else
    PLAYBACK_WindowPage = 0;    
    PLAYBACK_filelistWindow = TRUE;


    FileSys_WaitFinish();
    FileAttr = FileDB_CurrFile(FileDBHdl);
    FileDB_Refresh(FileDBHdl);
    PLAYBACK_Totlefilenum = FileDB_GetTotalFileNum(FileDBHdl);
   CheackTotleMoviePhotoFile(PLAYBACK_Totlefilenum);
    //PLAYBACK_TotlePhotoPage = FileDB_GetFilesNumByFileType(FileDBHdl, FILEDB_FMT_JPG)/6 +1;
   // PLAYBACK_TotleMoviePage = FileDB_GetFilesNumByFileType(FileDBHdl, FILEDB_FMT_MOV)/6 +1;
    switch(GetPlaybackFileType())
    {
    case MovieFile:
        if(g_uiOpenFilePage > PLAYBACK_TotleMoviePage)
            g_uiOpenFilePage = PLAYBACK_TotleMoviePage;
        break;
    case PhotoFile:
        if(g_uiOpenFilePage > PLAYBACK_TotlePhotoPage)
            g_uiOpenFilePage = PLAYBACK_TotlePhotoPage;
        break;
    }
   
    Update_FileList(g_uiOpenFilePage);
    
    Ux_DefaultEvent(pCtrl,NVTEVT_OPEN_WINDOW,paramNum,paramArray);
    return NVTEVT_CONSUME;
}
INT32 UIFlowWndPlayFileList_OnClose(VControl *pCtrl, UINT32 paramNum, UINT32 *paramArray)
{
PLAYBACK_filelistWindow = FALSE;
    Ux_DefaultEvent(pCtrl,NVTEVT_CLOSE_WINDOW,paramNum,paramArray);
    return NVTEVT_CONSUME;
}

