INT32 UIFlowWndPlayFileList_TP(VControl *pCtrl, UINT32 paramNum, UINT32 *paramArray)
{
UINT32 uiEvent;
    UINT32 P1,P2;
    UINT32  uiSoundMask;
  
    if(paramNum>0)
        uiEvent = paramArray[0];	
	
	switch(uiEvent)
	{

		case GESTURE_CKICK:
			P1= paramArray[1];
			P2=paramArray[2];  
		    if(TPIsOnRange(&UIFlowWndPlayFileList_Status_ReturnCtrl,P1,P2)==TRUE)
		    	{
		    	    Ux_OpenWindow((VControl *)(&UIFlowWndHomeCtrl), 0);
		    	     //Ux_CloseWindow(&UIFlowWndPlayFileListCtrl, 0);
								   
		    	}	
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile1Ctrl,P1,P2)==TRUE)
		    	{
		    	    Cheack_PlayFile(PLAYBACK_WindowPage,0);    
		    	     
		    	}	
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile2Ctrl,P1,P2)==TRUE)
		    	{
		    	Cheack_PlayFile(PLAYBACK_WindowPage,1); 
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile3Ctrl,P1,P2)==TRUE)
		    	{
		    	Cheack_PlayFile(PLAYBACK_WindowPage,2); 
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile4Ctrl,P1,P2)==TRUE)
		    	{
		    	Cheack_PlayFile(PLAYBACK_WindowPage,3); 
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile5Ctrl,P1,P2)==TRUE)
		    	{
		    	Cheack_PlayFile(PLAYBACK_WindowPage,4); 
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileListFile6Ctrl,P1,P2)==TRUE)
		    	{
		    	Cheack_PlayFile(PLAYBACK_WindowPage,5); 		       	
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileList_PrePageCtrl,P1,P2)==TRUE)
		    	{
		    	     if(PLAYBACK_WindowPage <= 0)
		    	        {
		    	        if(GetPlaybackFileType() == MovieFile)
                                PLAYBACK_WindowPage = PLAYBACK_TotleMoviePage -1;
                            else
                                PLAYBACK_WindowPage = PLAYBACK_TotlePhotoPage -1;
		    	        }
                          else
                            PLAYBACK_WindowPage --;
		       	Update_FileList(PLAYBACK_WindowPage);
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileList_NextPageCtrl,P1,P2)==TRUE)
		    	{
		    	    if(GetPlaybackFileType() == MovieFile)
		    	        {
        		    	     if(PLAYBACK_WindowPage >= PLAYBACK_TotleMoviePage -1)
                                    PLAYBACK_WindowPage = 0;
                                  else
                                PLAYBACK_WindowPage ++;
		    	        }
                        else
                            {
                                if(PLAYBACK_WindowPage >= PLAYBACK_TotlePhotoPage -1)
                                        PLAYBACK_WindowPage = 0;
                                      else
                                    PLAYBACK_WindowPage ++;
                            }
                         
		       	Update_FileList(PLAYBACK_WindowPage);
		       	
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileList_VideoCtrl,P1,P2)==TRUE)
		    	{
                        if(PLAYBACK_WindowType == MovieFile)
                        {

                        }
                        else
                        {
                        PLAYBACK_WindowType = MovieFile;
                        PLAYBACK_WindowPage = 0;
                        Update_FileList(PLAYBACK_WindowPage);
                        }
		       	
		    	}
                else  if(TPIsOnRange(&UIFlowWndPlayFileList_PhotoCtrl,P1,P2)==TRUE)
		    	{
                        if(PLAYBACK_WindowType == PhotoFile)
                        {

                        }
                        else
                        {
                        PLAYBACK_WindowType = PhotoFile;
                        PLAYBACK_WindowPage = 0;
                        Update_FileList(PLAYBACK_WindowPage);
                        }
		       	
		    	}
		
		
		break;
	}	  
	

	  
	return NVTEVT_CONSUME;
}

